		(val_add, ":displace", ":displ"),
		(val_add, ":displace", ":item_variant"),
		(troop_get_slot, ":new_item", "trp_merch_arms_array", ":displace"),
		(troop_get_slot, ":new_price", "trp_merch_arms_price_array", ":displace"),		
